import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../style/login.css'

function Login() {
  const navigate = useNavigate();

  const [login, setLogin] = useState({
    email: '',
    password: '',
  });

  const [invalidMessage, setInvalidMessage] = useState(false);

  const onTyping = ({ target: { name, value } }) => {
    setLogin({...login, [name]: value })
  }

  const loginBtn = () => {
    // request para o backend verificando as credenciais
    // se forem boas, direcionar para a página principal
    navigate('/main');
    // se forem ruins, alterar o invalidMessage para true
  }
  return (
    <div className="container">
      <form className="container-login">
        <label htmlFor="email" className="container-input-login">
          Nome de usuário ou e-mail:
          <input
            className="input-login"
            type="text"
            placeholder="Insira seu nome de usuário ou e-mail"
            id="email"
            name="email"
            value={ login.email }
            onChange = { onTyping }
          />
        </label>
        <label htmlFor="password" className="container-input-login">
          Senha:
          <input
            className="input-login"
            type="password"
            placeholder="Insira sua senha"
            id="password"
            name="password"
            value={ login.password }
            onChange = { onTyping }
          />
        </label>
        <button
          type="button"
          onClick={ loginBtn }
        >
          Login
        </button>
        <Link to="/register">Criar novo usuário</Link>
      </form>
      <p
        hidden={ !invalidMessage }
      >
        Usuário ou senha invalido(s)
      </p>
    </div>
  );
}

export default Login;
