import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import '../style/main.css'
import profitabilityFunc from '../utils/profitabilitySimulator'
import { Chart } from 'react-google-charts';
import Navbar from '../Components/Navbar';
import Header from '../Components/Header';

function Main() {

  const [clickedButton, setClickedButton] = useState('all');

  const profitability = profitabilityFunc();

  const mockAPIResponse = {
    netWorth: 1000000,
    profitability,
    assetAllocation: [
      ['Classe de ativo', 'classe'],
      ['Ações BR', 150000],
      ['Ações EUA', 150000],
      ['FIIs', 200000],
      ['Caixa', 200000],
      ['IPCA', 300000]
    ]
  }

  useEffect(() => {
    console.log(profitability);
  });

  const handleFilterClick = ({ target }) => {

  };

  return (
    <div className="container">
      <Header />
      <div className='content'>
        <Navbar  />
        <main className="main-content">
          <div className='initial-cards'>
            <div className='net-worth'>
              <h2>Patrimônio Líquido</h2>
              <h2> { mockAPIResponse.netWorth.toLocaleString('pt-br', { style: 'currency', currency: 'BRL' }) } </h2>
            </div>
            <div className='asset-allocation'>
              <h2>Distribuição de Carteira</h2>
              <Chart
                chartType='PieChart'
                width={'100%'}
                height={'90%'}
                data={mockAPIResponse.assetAllocation}
                col
                options={ {
                  // title: 'Distribuição de Carteira',
                  pieHole: 0,
                  is3D: false,
                  backgroundColor: '#282A3A',
                  colors: ['#54C3CF', '#F3B35A', '#FCD204', '#DFE06C', '#B0B0B0', '#929292', '#5D5D5D'],
                  legend: { textStyle: { color: 'white', fontSize: 16 } },
                  pieSliceTextStyle: {
                    fontSize: 16,
                    fontWeight: 'bold',
                    color: '#000080'
                  },
                } }
              />
            </div>
            <div className='market-info'>
              <h2>Maiores Variações</h2>
            </div>
          </div>
          <div className='profitability-chart'>
            <div className='data-filter'>
                <button name='oneYear' onClick={ handleFilterClick }>1 Ano</button>
                <button name='threeYears' onClick={ handleFilterClick }>3 Anos</button>
                <button name='all' onClick={ handleFilterClick }>Tudo</button>
            </div>
            <Chart
              chartType='LineChart'
              width='100%'
              height='95%'
              data={ mockAPIResponse.profitability }
              options={ {
                vAxis: { format: 'percent', gridlines: { color: 'none' }, textStyle: { color: 'white' } },
                hAxis: { textStyle: { color: 'white' } },
                title: 'Rentabilidade da Carteira',
                titleTextStyle: { color: 'white', fontSize: 24 },
                curveType: 'function',
                legend: { position: 'bottom', textStyle: { color: 'white' } },
                backgroundColor: '#282A3A',
                series: [{ color: '#1d9dff' }],
              } }
            />
          </div>
        </main>
      </div>
    </div>
  );
}

export default Main;
