import { useEffect } from 'react';
import '../style/main.css'
import profitability from '../utils/profitabilitySimulator'
import { Chart } from 'react-google-charts';

function Main() {

  const mockAPIResponse = {
    netWorth: 1000000,
    profitability: profitability(),
    assetAllocation: [
      ['Classe de ativo', 'classe'],
      ['Ações BR', 150000],
      ['Ações EUA', 150000],
      ['FIIs', 200000],
      ['Caixa', 200000],
      ['IPCA', 300000]
    ]
  }

  useEffect(() => {
    console.log(mockAPIResponse);
  })
  return (
    <div className="container">
      <header className="header">

      </header>
      <div className='content'>
        <nav className="nav-bar">

        </nav>
        <main className="main-content">
          <div className='net-worth'>
            <h2>Patrimônio Líquido</h2>
            <h2> { mockAPIResponse.netWorth.toLocaleString('pt-br', { style: 'currency', currency: 'BRL' }) } </h2>
          </div>
          <div className='asset-allocation'>
            <h2>Distribuição de Carteira</h2>
            <Chart
              chartType='PieChart'
              width='100%'
              height='400px'
              data={mockAPIResponse.assetAllocation}
              options={ {
                title: 'Distribuição de Carteira',
                pieHole: 0.4,
                is3D: false,
              } }
            />
          </div>
          <div className='profitability-chart'>

          </div>
        </main>
      </div>
    </div>
  );
}

export default Main;
