import { useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../style/main.css'
import profitabilityFunc from '../utils/profitabilitySimulator'
import { Chart } from 'react-google-charts';
import Navbar from '../Components/Navbar';
import Header from '../Components/Header';

function Main() {

  const profitability = profitabilityFunc();

  const mockAPIResponse = {
    netWorth: 1000000,
    profitability,
    assetAllocation: [
      ['Classe de ativo', 'classe'],
      ['Ações BR', 150000],
      ['Ações EUA', 150000],
      ['FIIs', 200000],
      ['Caixa', 200000],
      ['IPCA', 300000]
    ]
  }

  useEffect(() => {
    console.log(profitability);
  })
  return (
    <div className="container">
      <Header />
      <div className='content'>
        <Navbar  />
        <main className="main-content">
          <div className='initial-cards'>
            <div className='net-worth'>
              <h2>Patrimônio Líquido</h2>
              <h2> { mockAPIResponse.netWorth.toLocaleString('pt-br', { style: 'currency', currency: 'BRL' }) } </h2>
            </div>
            <div className='asset-allocation'>
              <h2>Distribuição de Carteira</h2>
              <Chart
                chartType='PieChart'
                width='100%'
                height='85%'
                data={mockAPIResponse.assetAllocation}
                col
                options={ {
                  // title: 'Distribuição de Carteira',
                  pieHole: 0.4,
                  is3D: false,
                  backgroundColor: '#282A3A',
                  colors: ['red', 'blue', 'yellow', 'green', 'grey'],
                  style: {

                  }
                } }
              />
            </div>
            <div className='market-info'>
              <h2>Maiores Variações</h2>
            </div>
          </div>
          <div className='profitability-chart'>
            <Chart
              chartType='LineChart'
              width='100%'
              height='100%'
              data={ mockAPIResponse.profitability }
              options={ {
                vAxis: { format: 'percent', gridlines: { count: 0 } },
                title: 'Rentabilidade da Carteira',
                curveType: 'function',
                legend: { position: 'bottom' },
                backgroundColor: '#282A3A',
                series: [{ color: 'red' }],
              } }
            />
          </div>
        </main>
      </div>
    </div>
  );
}

export default Main;
